mapper.sql:

select a.* from sys_equipment_update a


        and a.id = #{param.id,jdbcType=BIGINT}


        and a.name = #{param.name,jdbcType=VARCHAR}


        and a.hardware_version = #{param.hardwareVersion,jdbcType=VARCHAR}


        and a.software_version = #{param.softwareVersion,jdbcType=VARCHAR}


        and a.version_code like '%${versionCode}%'


        and a.path = #{param.path,jdbcType=VARCHAR}


        and a.remark = #{param.remark,jdbcType=VARCHAR}


        and a.update_personnel = #{param.updatePersonnel,jdbcType=VARCHAR}


    	and a.create_time > #{startTime}


    	and a.create_time < #{endTime}


order by a.id desc

mapper.select:

select 
id,
name,
hardware_version as hardwareVersion,
software_version as softwareVersion,
version_code as versionCode,
path,
remark,
update_personnel as updatePersonnel,
create_time as createTime 
from sys_equipment_update a


        and a.id = #{param.id,jdbcType=BIGINT}


        and a.name = #{param.name,jdbcType=VARCHAR}


        and a.hardware_version = #{param.hardwareVersion,jdbcType=VARCHAR}


        and a.software_version = #{param.softwareVersion,jdbcType=VARCHAR}


        and a.version_code like '%${versionCode}%'


        and a.path = #{param.path,jdbcType=VARCHAR}


        and a.remark = #{param.remark,jdbcType=VARCHAR}


        and a.update_personnel = #{param.updatePersonnel,jdbcType=VARCHAR}


    	and a.create_time > #{startTime}


    	and a.create_time < #{endTime}


order by a.hardware_version desc, a.create_time desc limit #{offset}, #{pageSize}


