mapper.select:
select 

from 
		sys_equipment_update 
where 
		version_code = (
			select max(version_code) 
			from   sys_equipment_update 
			where  hardware_version = #{hardwareVersion,jdbcType=VARCHAR} 
			and    software_version = #{softwareVersion,jdbcType=VARCHAR} 
		)

select 

from 
